import React, {useState, useEffect} from "react";
import { useParams } from "react-router-dom";
import lessons from "../data/lessons";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 40px;
  padding: 80px 25%;
`

const ModulePage = () => {
  const { id } = useParams();
  const moduleIndex = parseInt(id);
  const content = lessons[moduleIndex].lessons;

  const [currentLesson, setCurrentLesson] = useState(null);
  let progress = 0

  useEffect(() => {
    progress = parseInt(localStorage.getItem("progress")) || 0;

  })

  useEffect(() => {
    
    setCurrentLesson(content[progress * 10 / content.length]);
    console.log(content[progress * 10 / content.length]);
  }, [progress]);
  
  return (
    <Container>
      <h1>{currentLesson && currentLesson.title}</h1>
      {currentLesson && currentLesson.content.map((item, index) => (
        <div key={index}>
          <p style={{fontSize: 20}}>{item.text}</p>
          <div style={{background: '#4CAF50', height: '450px', width:'100%', margin: "20px 0"}}></div>
        </div>
      ))}
      
      <button onClick={() => {setCurrentLesson()}}>Next</button>

   
    
    </Container>
  );
};

export default ModulePage;
