import React from "react";
import styled from 'styled-components'
import { Link } from "react-router-dom";
import ProgressBar from "./ProgressBar";


const StyledLesson = styled.div`
  padding: 20px;
  border-radius: 15px;
  transition: 0.5s;

  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  gap: 15px;

  &:hover{
    background-color: #4CAF5020; 
  }

  h2, p{
      margin: 0;
  }
`

const AvailableButton = styled(Link)`
  display: block;
  background: #4CAF50;
  color: #eee;
  padding: 5px;
  text-align: center;
  border-radius:15px;
  cursor: pointer
`

const BlockedButton = styled(Link)`
  display: block;
  background: grey;
  color: #eee;
  padding: 5px;
  text-align: center;
  border-radius:15px;
`



const Module = ({ index, title, content, blocked, lessons }) => {
  return (
    <StyledLesson>
      <h2>{title}</h2>
      <p>{content}</p>
      <ul>{lessons.map((lesson, index) => {
        return <li key={index}>{lesson.title}</li>;
      })}</ul>
      {!blocked  ? <AvailableButton to={`/modules/${index}`}>Go to lesson</AvailableButton> : <BlockedButton>Blocked</BlockedButton>}

    </StyledLesson>
  );
};

export default Module;
