import React, {useState, useEffect} from "react";
import { useParams, Link } from "react-router-dom";
import lessons from "../data/lessons";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  gap: 40px;
  padding: 20px 120px;
`

const ModulePage = () => {
  const { id } = useParams();
  const moduleIndex = parseInt(id);
  const content = lessons[moduleIndex].lessons;

  const [currentLesson, setCurrentLesson] = useState(content[0]);

  useEffect(() => {
    let progress = localStorage.getItem('progress');
    console.log(currentLesson);
  }, []);
  
  return (
    <div>
      <h1>{currentLesson.title}</h1>
      <p>{currentLesson.goal}</p>
      {currentLesson.content.map((item, index) => (
        <div key={index}>
          <h2>{item.title}</h2>
          <p>{item.description}</p>
        </div>
      ))}
      <Link to={`/module/${moduleIndex}/lesson/${currentLesson.id + 1}`}>Next Lesson</Link>
      <Link to={`/module/${moduleIndex}/lesson/${currentLesson.id - 1}`}>Previous Lesson</Link>

      <button onClick={() => {
        setCurrentLesson(content[currentLesson.id + 1]);
        localStorage.setItem('progress', currentLesson.id + 1);
      }}>Next Lesson</button>
    
    </div>
  );
};

export default ModulePage;
