import React from "react";
import styled from 'styled-components'


const StyledLesson = styled.div`
  padding: 20px;
  border-radius: 15px;
  transition: 0.5s;

  display: flex;
  flex-direction: column;

  &:hover{
    background-color: #4CAF5020; 
  }

  h2, p{
      margin: 0;
  }

`

const Lesson = ({ title, content }) => {
  return (
    <StyledLesson>
      <h2>{title}</h2>
      <p>{content}</p>
    </StyledLesson>
  );
};

export default Lesson;
