import React from "react";
import styled from 'styled-components'
import { Link } from "react-router-dom";


const StyledLesson = styled.div`
  padding: 20px;
  border-radius: 15px;
  transition: 0.5s;

  display: flex;
  flex-direction: column;

  &:hover{
    background-color: #4CAF5020; 
  }

  h2, p{
      margin: 0;
  }
`

const AvailableButton = styled(Link)`
  display: block;
  background: #4CAF50;
  color: #eee;
  padding: 5px;
  text-align: center;
  border-radius:15px;
`


const Lesson = ({ title, content }) => {
  return (
    <StyledLesson>
      <h2>{title}</h2>
      <p>{content}</p>
    </StyledLesson>
  );
};

export default Lesson;
