import React, { useState } from "react";
import { useParams } from "react-router-dom";
import lessons from "../data/lessons";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 40px;
  padding: 80px 25%;
`;

const ModulePage = () => {
  const { id } = useParams();
  const moduleIndex = parseInt(id);
  const content = lessons[moduleIndex].lessons;

  const [progress, setProgress] = useState(
    localStorage.getItem("progress") || 0
  );
  const [currentLesson, setCurrentLesson] = useState(
    (progress * content.length) / 100
  );

  const [currentPage, setCurrentPage] = useState(0);

  return (
    <Container>
      <h1>{content[currentLesson] && content[currentLesson].title}</h1>
      {content[currentLesson] && (
        <div key={content[currentPage]} style={{ width: "100%" }}>
          <div
            style={{
              background: "#4CAF50",
              height: "450px",
              width: "100%",
              margin: "20px 0",
            }}></div>
          <p style={{ fontSize: 20 }}>
            {content[currentLesson].content[currentPage]}
          </p>
          <button onClick={() => setCurrentPage(currentPage + 1)}>Next</button>
        </div>
      )}

      {currentLesson <= content.length - 2 ? (
        <button
          onClick={() => {
            localStorage.setItem(
              "progress",
              ((currentLesson + 1) * 100) / content.length
            );
            setProgress(localStorage.getItem("progress"));
            setCurrentLesson(currentLesson + 1);
          }}>
          Next
        </button>
      ) : (
        <button
          onClick={() => {
            localStorage.setItem("progress", 100);
          }}>
          Finish Module
        </button>
      )}
    </Container>
  );
};

export default ModulePage;
