import React, { useState } from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom";
import modules from "../data/lessons";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 40px;
  padding: 80px 25%;
`;


const Lesson = () => {

  const {  id, lessonId } = useParams();
  const moduleIndex = parseInt(id);
  const content = modules[moduleIndex].lessons;

  const [progress, setProgress] = useState(
    localStorage.getItem("progress") || 0
  );
  const [currentLesson, setCurrentLesson] = useState(lessonId);
  const [currentPage, setCurrentPage] = useState(0)

  return (
    <Container>
    <h1>{content[currentLesson] && content[currentLesson].title}</h1>
    <h2>{content[currentLesson] && content[currentLesson].goal}</h2>

    {content[currentLesson] &&
    content[currentLesson].content.filter((item, index)=> index <= currentPage).map((item, index) => (
      <div key={index} style={{width: "100%"}}>
        {item.heading && <p>{item.heading}</p>}
        <div
          style={{
            background: "#4CAF50",
            height: "450px",
            margin: "20px 0",
          }}></div>

        {item.text.map((element, index) => {
          return(
          <div key={index} style={{marginBottom: 30}}>
            {element.heading && <h3>{element.heading}</h3>}
            <p  style={{ fontSize: 20, marginBottom: 20 }}>{element.text}</p>
          </div>
          )
          })}  
          
        
        
      </div>
    ))
     }
      {currentPage < content[currentLesson].content.length-1 ? <button
        onClick={() => setCurrentPage(currentPage+1)}>
        Next
      </button> : currentLesson <= content.length - 2 ? (
      <button
        onClick={() => {
          localStorage.setItem(
            "progress",
            ((currentLesson + 1) * 100) / content.length
          );
          setProgress(localStorage.getItem("progress"));
          setCurrentLesson(currentLesson + 1);
          setCurrentPage(0)
        }}>
        Next Lesson
      </button>
    ) : (
      <button
        onClick={() => {
          localStorage.setItem("progress", 100);
          setCurrentLesson(content.length-2)
          
        }}>
        Finish Module
      </button>
    )}

    
  </Container>
  )
}

export default Lesson
